#include <string>
using namespace std;

/*
Input: s = "aacecaaa"
Output: "aaacecaaa"
返回一个最短的palindrome
9:56->
从最左的开始查找palindrome，所能达到的最大长度=n,总长度l, 需要补l-n
如果最左未能组成任何palindrome,那么只能补
*/
class Solution0{
public:
    // TLE
    bool isPalinDrome(string &s, int start, int end) {
        while (start<end) {
            if (s[start]==s[end]) {
                start++;end--;
            } else {
                return false;
            }
            return true;
        }
    } 

    string shortestPalindrome(string s) {
        int n=s.length()-1;
        int pos=1;        
        string ans;
        for (int i=n;i>0;i--) {
            if (isPalinDrome(s, 0, i)) {
                pos = i+1;break;
            }
        }
        // for (int i=n;i>=pos;i--) {
        //     ans+=s[i];
        // }
        // return ans+s;

        string remain_rev = s.substr(pos);
        reverse(remain_rev.begin(), remain_rev.end());
        return remain_rev+s;
    }

    // better solution
    // 考虑到逐个比较palindrome比较慢
    // 对于palindrome，有时候使用reverse很有效
    string shortestPalindrome(string s) {
        int n = s.size();
        string rev(s);
        reverse(rev.begin(), rev.end());
        for (int i = 0; i < n; i++) {
            if (s.substr(0, n - i) == rev.substr(i))
                return rev.substr(0, i) + s;
        }
        return "";
    }
};


class Solution {
public:
    string shortestPalindrome(string s) {
        int n = s.size();
        int i = 0;
        // 来找出可能组成palindrome的范围(0,i)
        // abcdcba， 那么i==n (n为总长度)
        // abcdcbaF, i=n-1
        // abFcdcba, F无法找到对应的, i=2=> "ab", 这个是一个特例，也可能找不到palindrome
        // 通过递归来找出 ab=> b + a + b (因为最终总能够得到单个字符作为palindrome)
        for (int j = n - 1; j >= 0; j--) {
            if (s[i] == s[j])
                i++;
        }
        if (i == n) {
            return s;
        }
        string remain_rev = s.substr(i, n);
        reverse(remain_rev.begin(), remain_rev.end());
        return remain_rev + shortestPalindrome(s.substr(0, i)) + s.substr(i);
    }
};

